package examples

import (
	"encoding/hex"
	"log"
)

func GetIncrementExample() Example {
	// An implementation of the increment function.
	code, err := hex.DecodeString("")
	if err != nil {
		log.Fatalf("Unable to decode increment-code: %v", err)
	}

	return Example{
		Name:      "inc",
		code:      code,
		function:  0xDD5D5211,
		reference: inc,
	}
}

func inc(x int) int {
	return x + 1
}
